      let product = myLib.closestItemByClass(target, 'product');
      let previousBtnActive = product.querySelector('.product__description.is-active');
      let newPrice = target.getAttribute('data-product-description-price');
  
      previousBtnActive.classList.remove('is-active');
      target.classList.add('is-active');
